<template>
  <div class="AlarmDetails">
        <h1>{{alarmType}}报警</h1>
        <ul>
            <li v-for="(x,i) in alarmList" :key="i">
                <p><img :src="x.src"/> {{x.title}}</p>
                <p>{{x.value}}</p>
            </li>
        </ul>
  </div>
</template>

<script lang="ts">
import { Component, Vue } from "vue-property-decorator";
// 声明组件*****************
@Component
export default class AlarmDetails extends Vue {
  // 属性*****************
  private alarmList: Array<any> = [];

  // 钩子函数
  created ():void {
    document.title = '报警详情';
    this.alarmList = [
      {
        src: 'data:image/png;base64,',
        title: "报警时间",
        value: this['$route']['query']['alarm_time']
      },
      {
        src: 'data:image/png;base64,',
        title: "设备型号",
        value: this['$route']['query']['device_model']
      },
      {
        src: 'data:image/png;base64,',
        title: "设备ID",
        value: this['$route']['query']['device_id']
      },
      {
        src: 'data:image/png;base64,',
        title: "传输设备名称",
        value: this['$route']['query']['device_name']
      },
      {
        src: 'data:image/png;base64,',
        title: "设备位置",
        value: this['$route']['query']['alarm_address']
      },
    ]

    if(this['$route']['query']['alarm_type']==='water'){
      this.alarmList.splice(3, 0,
      {
        src: 'data:image/png;base64,',
        title: "告警值",
        value: this['$route']['query']['alarm_value']
      },
      {
        src: 'data:image/png;base64,',
        title: "报警类型",
        value: this['$route']['query']['device_static']
      });
    }
  }

  // computed
  get alarmType(): string {
    switch(this['$route']['query']['alarm_type']){
      case 'water':
        return '水压';
      case 'fire':
        return '火警';
      case 'fault':
        return '故障';
      default:
        return '';
    }
  }
}
</script>

